import { Component, OnInit } from '@angular/core';

interface Product {
  productName: string,
  productDate: Date,
  productPrice: number,
  productQuantity: number
  productAmount?: number
}

@Component({
  selector: 'app-list',
  templateUrl: './list.page.html',
  styleUrls: ['./list.page.scss'],
  standalone: false
})
export class ListPage implements OnInit {
  couponcode: string = '';
  strvalid: string = '';
  discount: number = 0;
  currentDate: Date = new Date();
  textcolor:string="red"
  fontsize='12px'

  books = [
    {
      image: 'data:image/jpeg;base64,',
      title: 'To Kill a Mockingbird',
      author: 'Harper Lee',
      discount: 10,
      publishedDate: new Date('1960-07-11'),
      price: 7.99,
      
    },
    {
      image: 'data:image/jpeg;base64,',
      title: 'The Great Gatsby',
      author: 'F. Scott Fitzgerald',
      discount: 5,
      publishedDate: new Date('1925-04-10'),
      price: 10.99,
      
    },
    {
      image: 'data:image/jpeg;base64,',
      title: 'Pride and Prejudice',
      author: 'Jane Austen',
      publishedDate: new Date('1813-01-28'),
      price: 12.75,
      discount: 15,
      
    }
  ]



  
  today_ind():string {
    const day_name = ["Minggu", "Senin", "Selasa", "Rabu", "Kamis", "Jumat", "Sabtu"];
    const day = day_name[this.currentDate.getDay()];
    const d = this.currentDate.getDate();
    const monthNames = ["Januari", "Februari", "Maret", "April", "Mei", "Juni", "Juli", "Agustus", "September", "Oktober", "November", "Desember"];
    const m = monthNames[this.currentDate.getMonth()];
    const y = this.currentDate.getFullYear();
    return day+', '+d+'-'+m+'-'+y;
  }

  product: Product = {
    productName: 'Iphone 14',
    productDate: new Date(),
    productPrice: 14000000,
    productQuantity: 1
  }

  

  checkValid() {
    if(this.couponcode=="1234") {
      this.strvalid = "Valid";
      this.discount = 5;
      this.textcolor = 'green'
      this.fontsize = '20px'
      
    }
    else if(this.couponcode=="6789") {
      this.strvalid = "Valid";
      this.discount = 10;
      this.textcolor = 'green'
      this.fontsize = '20px'
    }
    else {
      this.strvalid ="Invalid"
      this.discount = 0;
      this.textcolor = 'red'
      this.fontsize = '12px'
    }
  }

  reduceQuantity() {
    if (this.product.productQuantity > 1) {
      this.product.productQuantity--;
    }
  }

  addQuantity() {
    this.product.productQuantity++;
  }

  get productAmount(): number {
    return this.product.productPrice * this.product.productQuantity;
  }

  is5daysago = false;
  numberclicked = 0;
  is5dayslater = false;

  goYesterday() {
    this.currentDate.setDate(this.currentDate.getDate() - 1);
    this.numberclicked++;
    if (this.numberclicked == 5) this.is5daysago = true;
  }

  reset() {
    this.currentDate = new Date();
    this.numberclicked = 0;
    this.is5daysago = false;
    this.is5dayslater = false;
  }

  goTomorrow() {
    this.currentDate.setDate(this.currentDate.getDate() + 1);
    this.numberclicked++;
    if (this.numberclicked == 5) this.is5dayslater = true;
  }

  constructor() { }

  ngOnInit() {
  }
}
